import { Avatar, Box, Container, MenuItem, TextField, Typography } from '@mui/material'
import React from 'react'
import AddBoxIcon from '@mui/icons-material/AddBox';
import { TimePicker } from '@mui/x-date-pickers';
const typecases = [
    {
      value: '0',
      label: 'Criminal',
    },
    {
      value: '1',
      label: 'Elder',
    },
    {
      value: '2',
      label: 'Banking',
    },

  ];





function Addform() {
  return (
    <Container maxWidth='lg' sx={{backgroundColor:'#F1F1F1'}}> 
         <Box
          sx={{
            marginTop: 8,
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
          }}>
            <Avatar sx={{ m: 1, bgcolor: 'secondary.main' }}>
            <AddBoxIcon /> 
          </Avatar>
          <Typography component='h2' variant='h5'> Add Cases </Typography>
          <Container>
          <TextField label="Case number" fullWidth variant='filled' sx={{marginTop:'20px'}} size='normal'/>
          <TextField label="Name of the case" fullWidth variant='filled' sx={{marginTop:'20px'}} size='normal'/>
          <TextField label="Brief introduction of case" fullWidth variant='filled' sx={{marginTop:'20px'}} multiline rows={5}/>
          </Container>
          <Container>
          <TextField
           label="Type of case"  variant='filled' sx={{marginTop:'20px', width:'550px'}} size='normal' select
        >

          {typecases.map((option) => (
            <MenuItem key={option.value} value={option.value}>
              {option.label}
            </MenuItem>
          ))}
        </TextField>
        <LocalizationProvider dateAdapter={AdapterDayjs}>
      <DemoContainer components={['TimePicker']}>
        <TimePicker label="Basic time picker" />
      </DemoContainer>
    </LocalizationProvider>
        </Container>  



        </Box>
    </Container>
  )
}

export default Addform
